import React, { useEffect } from "react"
import { ReactSVG } from "react-svg"
import { Image } from "rebass"
import { baseUrl } from "../../utils/helper"

export default props => {
	const { width, svgUrl, shadowUrl, key, color } = props
	let svgElement = false
	useEffect(() => {
		if (!svgElement) return
		for (let i = 0; i < svgElement.children.length; i++) {
			if (
				svgElement.children[i].tagName === "g" ||
				svgElement.children[i].tagName === "path"
			) {
				svgElement.children[i].style.fill = color.type
					? `url("#${color._id}")`
					: color.value
			}
		}
	}, [color])
	return (
		<div
			style={{
				position: "relative",
				width: width
			}}
		>
			<Image
				src={baseUrl + shadowUrl}
				sx={{
					width: width,
					position: "absolute"
				}}
			/>
			<ReactSVG
				style={{
					width: width,
					minWidth: "14px"
				}}
				afterInjection={(error, svg) => {
					if (error) {
						console.error(error)
						return
					}
					svgElement = svg
					for (let i = 0; i < svg.children.length; i++) {
						if (
							svg.children[i].tagName === "g" ||
							svg.children[i].tagName === "path"
						) {
							svg.children[i].style.fill = color.type
								? `url("#${color._id}")`
								: color.value
						}
					}
				}}
				renumerateIRIElements={false}
				loading={() => {
					console.log("loading")
					return "loading"
				}}
				beforeInjection={svg => {
					svg.setAttribute("id", key)
					svg.setAttribute("style", `width: ${width}; height: 100%`)
				}}
				evalScripts="always"
				src={baseUrl + svgUrl}
			/>
		</div>
	)
}
