import React, { useState, useEffect } from "react"
import { Flex, Text, Box, Button, Image } from "rebass"
import { Input } from "@rebass/forms"
import Head from "../components/head"
import request from "../utils/request.js"
import useUserInfo from "../hooks/getUserInfo"
import Router from "next/router"
import Svg from "../components/svg"
export default () => {
	const [modal, setMadal] = useState(0) // 0, 1, 2, 3
	const [svgColor, setSvgColor] = useState("#231815")
	const [account, setAccount] = useState("chanpin2@miss.com")
	const [password, setPassword] = useState("123456")
	const showInfoList = [
		{
			signal: "AT MRMISS WE LOVE . . .",
			leftColor: "#E74C39",
			rightColor: "#231815",
			imgUrl: "/public/1/t-1.png"
		},
		{
			signal: "EVERY DESIGN",
			leftColor: "#FF8E6C",
			rightColor: "#B6141E",
			imgUrl: "/public/1/t2.png"
		},
		{
			signal: "EVERY PROJECT",
			leftColor: "#434343",
			rightColor: "#784E38",
			imgUrl: "/public/1/t3.png"
		},
		{
			signal: "EVERY PRO",
			leftColor: "#000000",
			rightColor: "#374E4B",
			imgUrl: "/public/1/t4.png"
		}
	]
	const handleLogin = async () => {
		const req = await request(
			"user/login",
			{
				account: "chanpin3@miss.com",
				password: "123456"
			},
			"post"
		)
		console.log(req)
		if (req) {
			Router.push("/main")
		}
	}
	const userInfo = useUserInfo()
	console.log(userInfo, "userInfo")

	return (
		<React.Fragment>
			<Head></Head>
			<Box height="100vh">
				<Flex flexWrap="wrap" height={["100%", "62.5vh"]}>
					<Flex
						flexDirection="column"
						justifyContent="space-between"
						width={[1, 722 / 1920]}
						height={["100%", "62.5vh"]}
						bg="white"
					>
						<Box
							width="1.8rem"
							height="0.53rem"
							sx={{
								margin: "10% 0 0 10%",
								backgroundImage: 'url("/1/LOGO.png")',
								backgroundRepeat: "no-repeat",
								backgroundSize: "100% 100%"
							}}
						/>
						<Box padding="0 0 10% 10%">
							<Flex alignItems="center" mb={"2%"}>
								<Image
									src="./1/user-icon.png"
									mr={"10px"}
									sx={{
										width: "0.18rem",
										minWidth: "14px",
										height: "0.18rem",
										minHeight: "14px"
									}}
								/>
								<Text fontSize="0.14rem">ACCOUNT NUMBER</Text>
							</Flex>

							<Input
								width="3rem"
								height="0.38rem"
								id="username"
								value={account}
								onChange={e => setAccount(e.target.value)}
								name="username"
								type="text"
							/>

							<Flex alignItems="center" mt="4%" mb={"2%"}>
								<Image
									src="./1/password-icon.png"
									mr={"10px"}
									sx={{
										width: "0.18rem",
										minWidth: "14px",
										height: "0.18rem",
										minHeight: "14px"
									}}
								/>
								<Text fontSize="0.14rem">PASSWORD</Text>
							</Flex>

							<Input
								width="3rem"
								height="0.38rem"
								id="password"
								name="password"
								type="password"
								value={password}
								onChange={e => setPassword(e.target.value)}
							/>
							<Button
								variant="primary"
								width="1.34rem"
								height="0.38rem"
								bg="#000000"
								color="#ffffff"
								mt={"5.8%"}
								onClick={() => handleLogin()}
								padding="0"
								sx={{
									borderRadius: 0,
									fontSize: "0.14rem"
								}}
							>
								LOADING
							</Button>
						</Box>
					</Flex>
					<Box width={[1, 718 / 1920]} bg="gray">
						<Image
							src="./1/user-icon.png"
							mr={"10px"}
							sx={{
								width: "0.18rem",
								minWidth: "14px",
								height: "0.18rem",
								minHeight: "14px"
							}}
						/>
					</Box>
					<Box
						width={[1, 480 / 1920]}
						bg={showInfoList[modal].rightColor}
					></Box>
				</Flex>
				<Flex flexWrap="wrap" height={[0, "37.5vh"]}>
					<Box width={[1, 722 / 1920]} bg={showInfoList[modal].leftColor}></Box>
					<Flex
						alignItems="center"
						justifyContent="center"
						width={[1, 718 / 1920]}
						bg="white"
						fontSize="0.38rem"
					>
						<Flex flexDirection="column">
							{showInfoList.map((info, index) => (
								<Text
									sx={{
										textDecoration: index === modal ? "underline" : "none"
									}}
									onMouseEnter={() => {
										setMadal(index)
									}}
								>
									{info.signal}
								</Text>
							))}
						</Flex>
					</Flex>
					<Flex
						width={[1, 480 / 1920]}
						bg="#DCDCDC"
						justifyContent="center"
						alignItems="center"
					>
						<Svg color={showInfoList[modal].rightColor} />
					</Flex>
				</Flex>
			</Box>
		</React.Fragment>
	)
}
