import React, { useEffect, useState } from "react"
import ReactSVG from "../components/commons/react-svg"
import StyleImg from "../components/commons/style-img"
import { Flex, Text, Box, Image, Button } from "rebass"

import Modal from "./modal"
import { ColorList, PaintList } from "./color-and-paint"

import request from "../utils/request.js"
import { baseUrl } from "../utils/helper"
export default props => {
	const { styleDetails, curStyle, userInfo } = props
	let editSvgs = {}
	let editPatterns = {}
	let tempSelectedIds = [curStyle[0].color]
	// let tempSelectedImgs = [curStyle[0].imgUrl]
	if (curStyle.length > 1) {
		tempSelectedIds.push(curStyle[1].color)
		// tempSelectedImgs.push(curStyle[1].imgUrl)
	} else {
		tempSelectedIds.push(false)
		// tempSelectedImgs.push(false)
	}
	const [curSelectedColors, setCurSelectedColors] = useState(tempSelectedIds)
	const [curStyleIndex, setCurStyleIndex] = useState(0)
	// const [curStyleImgs, setCurStyleImgs] = useState(tempSelectedImgs)
	const [plainColors, setPlainColors] = useState({ docs: [] })
	const [flowerColors, setFlowerColors] = useState({ docs: [] })
	// const [colorList, setColorList] = useState(styleDetails[0].plainColors)
	const getColorList = async page => {
		const req = await request(
			"color/getList",
			{ type: 0, page, limit: 14 },
			"get"
		)
		setPlainColors(req)
		// console.log("getChannels", req)
	}
	const getPaintList = async page => {
		const req = await request(
			"color/getList",
			{ type: 1, page, limit: 14 },
			"get"
		)
		setFlowerColors(req)
		// console.log("getChannels", req)
	}
	const getEditSvgById = id => {
		if (!editSvgs[id]) {
			let svg = document.getElementById(id)
			editSvgs[id] = svg
		}
		return editSvgs[id]
	}

	const createSvgPattern = (svg, color) => {
		if (!editSvgs.svgDefs) {
			editSvgs.svgDefs = document.createElementNS(
				"http://www.w3.org/2000/svg",
				"defs"
			)
			document.getElementById("mm-defs-svg").appendChild(editSvgs.svgDefs)
		}
		if (!editPatterns[color._id]) {
			let svgPattern = document.createElementNS(
				"http://www.w3.org/2000/svg",
				"pattern"
			)

			// this.svgPatternImage = document.createElementNS('http://www.w3.org/2000/svg', 'image');
			svgPattern.setAttribute("id", color._id)
			editPatterns[color._id] = svgPattern
			svgPattern.setAttribute("patternUnits", "userSpaceOnUse")
			svgPattern.setAttribute("patternContentUnits", "userSpaceOnUse")
			const imgVals = { scale: 1 }

			let W = ((color.size * svg.width.baseVal.value) / 20) * imgVals.scale
			let H = (W * color.height) / color.width

			svgPattern.setAttribute("width", `${W}px`)
			svgPattern.setAttribute("height", `${H}px`)
			// this.getSvgPatternAndImage().svgPatternImage.href.baseval = `${api}/${color.value}`
			let svgPatternImage = document.createElementNS(
				"http://www.w3.org/2000/svg",
				"image"
			)

			svgPatternImage.setAttribute("width", `${W}px`)
			svgPatternImage.setAttribute("height", `${H}px`)
			svgPatternImage.href.baseVal = `${baseUrl}${color.value}`

			editSvgs.svgDefs.appendChild(svgPattern)
			svgPattern.appendChild(svgPatternImage)
		}
	}

	const selectFlowerColor = (color, svgId) => {
		// this.getSvgPatternAndImage()
		// let svg = getEditSvgById(svgId)

		if (!editPatterns[color._id]) {
			createSvgPattern(svg, color)
		}
		// if (svg.svgPatternImage) {
		// 	svg.svgPattern.removeChild(svg.svgPatternImage)
		// }

		// this.svgFrontEditBlocks.map(item => {
		// 	item.style.fill = 'url("#avatar")'
		// })
		// for (let i = 0; i < svg.children.length; i++) {
		// 	if (
		// 		svg.children[i].tagName === "g" ||
		// 		svg.children[i].tagName === "path"
		// 	) {
		// 		svg.children[i].style.fill = `url("#${color._id}")`
		// 	}
		// }
	}
	const handleChangeColorPage = (page, type) => {
		console.log(page, type)
		if (type === 0) {
			getColorList(page)
		} else {
			getPaintList(page)
		}
	}
	const handleSelect = (item, type) => {
		console.log(curSelectedColors)
		if (item._id === curSelectedColors[curStyleIndex]) {
			curSelectedColors[curStyleIndex] = false
			setCurSelectedColors([].concat(curSelectedColors))
			// curStyleImgs.splice(curStyleIndex, 1, false)
			// setCurStyleImgs([].concat(curStyleImgs))
			console.log(curSelectedColors)
		} else {
			curSelectedColors[curStyleIndex] = item
			setCurSelectedColors([].concat(curSelectedColors))
			// setCurSelectedColorId()
			switch (type) {
				case "color":
					const cIndex = styleDetails[curStyleIndex]._id
					let svg = getEditSvgById(cIndex)
					// let section = document.getElementById("section")
					// section.innerHTML = "hhhhhh"
					for (let i = 0; i < svg.children.length; i++) {
						if (
							svg.children[i].tagName === "g" ||
							svg.children[i].tagName === "path"
						) {
							svg.children[i].style.fill = item.value
							console.log(svg.children[i])
							// svg.removeChild(svg.children[i])
						}
					}

					break
				case "paint":
					const pIndex = styleDetails[curStyleIndex]._id
					selectFlowerColor(item, pIndex)
					break
				default:
					break
			}
		}
	}

	useEffect(() => {
		const getChannelsAssign = async (styleId, channelId) => {
			const req = await request(
				"channel/getAssign",
				{
					styleId,
					channelId
				},
				"get"
			)
			if (!req) {
				setPlainColors({
					docs: [],
					page: 1
				})
				setFlowerColors({
					docs: [],
					page: 1
				})
			} else {
				setPlainColors({
					docs: req.plainColors,
					page: 1
				})
				setFlowerColors({
					docs: req.flowerColors,
					page: 1
				})
			}
		}

		if (userInfo.role === 0) {
			// 客户
			getChannelsAssign(sid, userInfo.channels[0])
		} else {
			// 产品经理
			getColorList(1)
			getPaintList(1)
		}
	}, [])

	useEffect(() => {}, [])
	return (
		<Modal onClose={props.onClose}>
			<Box width="14rem" fontSize="0.18rem" color="#000">
				<Flex justifyContent="space-between">
					<Flex
						flexDirection="column"
						alignItems="center"
						justifyContent="space-evenly"
						width="6.75rem"
						sx={{
							cursor: "pointer"
						}}
					>
						{Array.isArray(styleDetails) &&
							styleDetails.map((style, index) => (
								<Flex
									width="6.75rem"
									padding="1rem 0"
									alignItems="center"
									justifyContent="center"
									sx={{
										position: "relative",
										border:
											curStyleIndex === index
												? "1px solid #000"
												: "1px solid #A0A0A0"
									}}
									onClick={() => {
										setCurStyleIndex(index)
									}}
								>
									<StyleImg
										width="3.79rem"
										color={curSelectedColors[index]}
										svgId={style._id}
										shadowUrl={style.shadowUrl}
										svgUrl={style.svgUrl}
									/>
									{/* <div
										style={{
											position: "relative"
										}}
									>
										<Image
											src={baseUrl + style.shadowUrl}
											sx={{
												width: "3.79rem",
												minWidth: "14px",
												minHeight: "14px",
												position: "absolute"
											}}
										/>
										<ReactSVG
											style={{
												width: "3.79rem",
												minWidth: "14px",
												minHeight: "14px"
											}}
											beforeInjection={svg => {
												svg.setAttribute("id", style._id)
												svg.setAttribute("style", "width: 100%; height: 100%")
											}}
											renumerateIRIElements={false}
											loading={() => {
												console.log("loading")
												return "loading"
											}}
											evalScripts="always"
											src={baseUrl + style.svgUrl}
										></ReactSVG>
									</div> */}
								</Flex>
							))}
					</Flex>

					<Flex flexDirection="column" flexGrow={1} pl="0.6rem">
						<Box width={[1]} mb="0.6rem">
							<Text id="section" fontSize="0.18rem" fontWeight="bolder">
								SECTION NUMBER
							</Text>
							<Text fontSize="0.1rem" m="0.16rem 0">
								{styleDetails[curStyleIndex].styleNo}
							</Text>
						</Box>
						<ColorList
							colorList={plainColors.docs || []}
							handleSelect={handleSelect}
							curChannelId={0}
							selectedList={[curSelectedColors[curStyleIndex] || {}]}
							page={plainColors.page}
							onChangePage={handleChangeColorPage}
						/>
						<Box height="0.3rem" width={[1]} />
						<PaintList
							paintList={flowerColors.docs || []}
							handleSelect={handleSelect}
							curChannelId={0}
							selectedList={[curSelectedColors[curStyleIndex] || {}]}
							page={flowerColors.page}
							onChangePage={handleChangeColorPage}
						/>
						<Button
							variant="primary"
							height="0.7rem"
							width="100%"
							bg="#1B1B1B"
							color="#fff"
							padding="0"
							mt="0.7rem"
							sx={{
								borderRadius: 0,
								fontSize: "0.14rem",
								cursor: "pointer"
							}}
							onClick={() => {
								if (!curSelectedColors[0]) return
								if (styleDetails.length > 1 && !curSelectedColors[1]) return
								props.confirmMade(curSelectedColors)
								props.onClose()
							}}
						>
							FINISH
						</Button>
					</Flex>
				</Flex>
			</Box>
		</Modal>
	)
}
