							width="6.75rem"
							height="4.88rem"
							alignItems="center"
							justifyContent="center"
						>
							<Image
								src="./4/style1.png"
								sx={{
									width: "3.89rem",
									minWidth: "14px",
									minHeight: "14px"
								}}
							/>
						</Flex>
					</Flex>

					<Flex flexDirection="column">
						<ColorList colorList={colorList} handleSelect={() => {}} />
						<PaintList paintList={paintList} handleSelect={() => {}} />
					</Flex>
				</Flex>
			</Box>
		</Modal>
	)
}
