import React, { useState, useEffect } from "react"
import { Flex, Text, Box, Button, Row, Column } from "rebass"
import Table, { TableLine, ProductInfo } from "./base-table"
import StyleItem from "../commons/min-style-item"
// import InputNumber from "../number-input"
// import request from "../../utils/request"

export default props => {
	const { OrderDetail } = props
	const line = props.OrderDetail.orderData.length
	// const {orderData} = OrderDetail
	console.log(OrderDetail)
	const [orderData, setOrderData] = useState(OrderDetail.orderData)
	const [packageCount, setPackageCount] = useState(OrderDetail.packageCount)
	const handleChangePackageCount = num => {
		if (num < 1) return
		setPackageCount(num)
		orderData.map((order, index) => {
			order.total = getItemsTotal(index) * num
		})
		setOrderData([].concat(orderData))
	}
	const handleChangeOrder = (styleIndex, sizeIndex, num) => {
		console.log(num)
		if (num < 0) return
		orderData[styleIndex].sizeInfo[sizeIndex].num = parseInt(num, 10)
		let allNumSum = getItemsTotal(styleIndex)
		orderData[styleIndex].total = allNumSum * packageCount
		let temp = new Number(
			allNumSum * packageCount * orderData[styleIndex].signalPrice
		)
		orderData[styleIndex].totalPrice = temp.toFixed(2)
		setOrderData([].concat(orderData))
		// console.log("orderData", orderData)
	}
	const getItemsTotal = index => {
		if (index < orderData.length) {
			let count = 0
			orderData[index].sizeInfo.map(size => {
				count += size.num
			})
			return count
		}
	}

	return (
		<Flex
			flexDirection="column"
			justifyContent="space-between"
			sx={{
				cursor: "pointer",
				height: "100%"
			}}
		>
			<Box
				sx={{
					padding: "0 18px 18px 18px",
					height: "max-content",
					width: "800px",
					display: "table"
				}}
			>
				<Table
					haveBorder
					sx={{ margin: "0", marginRight: "1px", width: "100%" }}
					titles={[
						{ name: "00", width: "2/22", isHide: true },
						{ name: "SECTION NUMBER", width: "2/22" },
						{ name: "COLOUR", width: "4/22" },
						{ name: "IMAGE", width: "1/22" },
						{ name: "SIZE", width: "2/22" },
						{ name: "PACKAGES", width: "2/22" },
						{ name: "QUANTITY", width: "2/22" },
						{ name: "PRICE", width: "4/22" },
						{ name: "TOTAL AMOUN", width: "1/22" }
					]}
				>
					{orderData.map((item, index) => (
						<>
							<TableLine noEdit key={`selectline-${item._id}`}>
								<Text rowspan={2} hasBorder={"1px solid"}>
									{index}
								</Text>
								<ProductInfo
									rowspan={2}
									styleNum="XSJHFH00928"
									hasBorder={"1px solid"}
								/>
								<Flex
									rowspan={2}
									flexDirection="column"
									hasBorder={"1px solid"}
								>
									<ProductInfo made="2110 YE GREEN" />
									<ProductInfo made="2110 YE GREEN" />
								</Flex>
								<StyleItem
									rowspan={2}
									hasBorder={"1px solid"}
									margin={"1px"}
									key={`${index}-style-img`}
									styleList={item.favorite.styleAndColor.map(x => {
										// styleList.push({ style: x.style, colors: x.colorIds })
										return { style: x.styleId, colors: x.colorIds }
									})}
									index={index}
									tool={false}
								/>
								<Flex
									rowspan={1}
									// colspan={item.sizeInfo.length}
									justifyContent="space-between"
									hasBorder={"1px solid"}
								>
									{item.sizeInfo.map((size, sizeIndex) => (
										<Flex flexDirection="column">
											<Text>{size.name}</Text>
											{/* <Text>{item.sizeInfo[sizeIndex].num}</Text> */}
										</Flex>
									))}
								</Flex>
								{index > 0 ? null : (
									<Flex
										rowspan={line * 2}
										hasBorder={"1px solid"}
										justifyContent="center"
										alignItems="center"
										sx={{ width: "100%", height: "100%" }}
									>
										<Text>{packageCount}</Text>
										{/* <InputNumber value={packageCount} /> */}
									</Flex>
								)}

								<Text rowspan={2} hasBorder={"1px solid"}>
									{item.total}
								</Text>
								<Flex
									rowspan={2}
									hasBorder={"1px solid"}
									flexDirection="column"
								>
									{item.signalPrice}
								</Flex>
								<Text rowspan={2} hasBorder={"1px solid"}>
									{item.totalPrice}
								</Text>
							</TableLine>
							<tr noEdit key={`selectline-1-${item._id}`}>
								<td>
									<Flex justifyContent="space-between" hasBorder={"1px solid"}>
										{item.sizeInfo.map(size => (
											<Text>{size.num}</Text>
										))}
									</Flex>
								</td>
							</tr>
						</>
					))}
				</Table>
			</Box>
		</Flex>
	)
}
