import React, { useEffect, useState } from "react"
import { Flex, Text, Box, Button, Image } from "rebass"
import NavItem from "../components/nav-item"

const AssignItem = props => (
	<Box
		sx={{
			"& :hover": {
				background: "#FF8E6C"
			}
		}}
	>
		<Text fontSize="0.18rem" fontWeight="bolder">
			{props.title}
		</Text>
	</Box>
)
const Title = props => (
	<Flex height="1rem" width="100%" sx={{}}>
		<Box>
			<Text fontSize="0.18rem" fontWeight="bolder">
				SECTION NUMBER
			</Text>
			<Text fontSize="0.1rem" m="0.18rem 0">
				2010000XJLS
			</Text>
		</Box>
		<Image
			src="/3/close.png"
			width="0.23rem"
			sx={{ position: "absolute", right: "0.42rem", top: "0.41rem" }}
		></Image>
	</Flex>
)
export default props => {
	const [showScroll, setShowScroll] = useState(false)
	return (
		<Flex
			width="6.4rem"
			bg="#fff"
			fontSize="0.18rem"
			color="#000"
			p="0.5rem 0.7rem "
			sx={{
				position: "absolute",
				top: "40px",
				left: "calc(6.4rem)"
			}}
		>
			<Title></Title>
		</Flex>
	)
}
