import React, { useEffect, useState } from "react"
import { Tab, Tabs, TabList, TabPanel } from "react-tabs"
import { Flex } from "rebass"
import ChannelTable from "../tables/channel-table"
import UserTable from "../tables/user-table"
import request from "../../utils/request.js"

export default () => {
	const [userList, setUserList] = useState([])
	const [channelList, setChannelList] = useState([])
	useEffect(() => {
		const getUserChannels = async page => {
			const req = await request(
				"user/getUserChannels",
				{ type: 0, page, limit: 14 },
				"get"
			)
			// setPlainColors(req)
			console.log("getUserChannels", req)
		}
		getUserChannels()
	}, [])
	return (
		<Flex
			textAlign="center"
			sx={{
				"&": {
					".react-tabs__tab": {
						background: "#FFF0E5",
						color: "#000",
						height: "100%",
						flexGrow: 1
					},
					".react-tabs__tab--selected": {
						flexGrow: 1,
						background: "#FF8E6C !important"
					}
				}
			}}
		>
			<Tabs style={{ flexGrow: 1, display: "flex", flexDirection: "column" }}>
				<TabList
					style={{
						display: "flex",
						height: "0.4rem",
						minHeight: "24px",
						// lineHeight: "24px",
						padding: "0",
						margin: "0",
						background: "#FFF0E5",
						fontSize: "0.2rem",
						color: "#000",
						justifyContent: "space-around",
						alignItems: "center",
						listStyleType: "none"
					}}
				>
					<Tab
						style={{
							display: "flex",
							justifyContent: "center",
							alignItems: "center"
						}}
					>
						CHANNEL LIST
					</Tab>
					<Tab
						style={{
							display: "flex",
							justifyContent: "center",
							alignItems: "center"
						}}
					>
						CUSTOMER LIST
					</Tab>
				</TabList>
				<TabPanel>
					<ChannelTable />
				</TabPanel>
				<TabPanel>
					<UserTable />
				</TabPanel>
			</Tabs>
		</Flex>
	)
}
