import React, { useEffect, useState } from "react"
import { Flex, Text, Box, Image } from "rebass"

import Modal from "./modal"
import { ColorList, PaintList } from "./color-and-paint"

import request from "../utils/request.js"
export default props => {
	const [colorList, setColorList] = useState([])
	const [paintList, setPaintList] = useState([])
	useEffect(() => {
		if (props.currentSeleted) {
			const getStyle = async () => {
				const req = await request("style/detail", { _id: props.sid }, "get")
				if (!req) return
				console.log("getStyle", req)
				setPaintList(req.flowerColors)
				setColorList(req.plainColors)
			}
			getStyle()
		}
	}, [])
	return (
		<Modal onClose={props.onClose}>
			<Box width="14rem" fontSize="0.18rem" color="#000">
				<Flex justifyContent="space-between">
					<Flex
						flexDirection="column"
						alignItems="center"
						justifyContent="space-evenly"
						width="6.75rem"
						sx={{
							cursor: "pointer"
						}}
					>
						<Flex
							width="6.75rem"
							height="4.88rem"
							alignItems="center"
							justifyContent="center"
						>
							<Image
								src="/4/style2.png"
								sx={{
									width: "3.79rem",
									minWidth: "14px",
									minHeight: "14px"
								}}
							/>
						</Flex>
						<Flex
							width="6.75rem"
							height="4.88rem"
							alignItems="center"
							justifyContent="center"
						>
							<Image
								src="./4/style1.png"
								sx={{
									width: "3.89rem",
									minWidth: "14px",
									minHeight: "14px"
								}}
							/>
						</Flex>
					</Flex>

					<Flex flexDirection="column">
						<ColorList colorList={colorList} handleSelect={() => {}} />
						<PaintList paintList={paintList} handleSelect={() => {}} />
					</Flex>
				</Flex>
			</Box>
		</Modal>
	)
}
