import React, { useEffect, useState } from "react"
import { Flex, Text, Box, Button, Image } from "rebass"
import NavItem from "../components/nav-item"

const AssignItem = props => (
	<Box
		sx={{
			"& :hover": {
				background: "#FF8E6C"
			}
		}}
	>
		<Image src={props.src} p="0.68rem" sx={{ display: "block" }}></Image>
	</Box>
)
const Title = props => (
	<Flex
		height="1rem"
		width="100%"
		sx={{
			"& :hover": {
				background: "#FF8E6C"
			}
		}}
	>
		<Box>
			<Text fontSize="0.18rem">SECTION NUMBER</Text>
			<Text fontSize="0.1rem">2010000XJLS</Text>
		</Box>
		<Image src="/3/close.png" p="0.68rem" sx={{ display: "block" }}></Image>
	</Flex>
)
export default props => {
	const [showScroll, setShowScroll] = useState(false)
	return (
		<Flex
			width="100%"
			bg="#fff"
			fontSize="0.18rem"
			color="#000"
			sx={{
				position: "absolute",
				top: "40px",
				left: "calc(40px + 3.22rem)"
			}}
		>
			<Title></Title>
		</Flex>
	)
}
