import React, { useEffect, useState } from "react"
import { Flex, Text, Box, Image } from "rebass"

import Modal from "./modal"
import { ColorList, PaintList } from "./color-and-paint"

import request from "../utils/request.js"
import { baseUrl } from "../utils/helper"
export default props => {
	const { styleDetails } = props
	console.log("made-edit-box")
	console.log(styleDetails[0].flowerColors)
	const [curStyleIndex, setCurStyleIndex] = useState(0)
	const [curStyleImgs, setCurStyleImgs] = useState([false, false])
	// const [colorList, setColorList] = useState(styleDetails[0].plainColors)
	const handleSelect = (item, type) => {
		switch (type) {
			case "color":
				const cIndex = styleDetails[curStyleIndex].plainColors.findIndex(
					x => x._id === item._id
				)

				curStyleImgs.splice(
					curStyleIndex,
					1,
					styleDetails[curStyleIndex].plainColors[cIndex].front
				)
				console.log("curStyleImgs~~~~")
				console.log(curStyleImgs)
				setCurStyleImgs([].concat(curStyleImgs))
				break
			case "paint":
				const pIndex = styleDetails[curStyleIndex].flowerColors.findIndex(
					x => x._id === item._id
				)

				curStyleImgs.splice(
					curStyleIndex,
					1,
					styleDetails[curStyleIndex].flowerColors[cIndex].front
				)
				setCurStyleImgs([].concat(curStyleImgs))
				break
			default:
				break
		}
	}

	return (
		<Modal onClose={props.onClose}>
			<Box width="14rem" fontSize="0.18rem" color="#000">
				<Flex justifyContent="space-between">
					<Flex
						flexDirection="column"
						alignItems="center"
						justifyContent="space-evenly"
						width="6.75rem"
						sx={{
							cursor: "pointer"
						}}
					>
						{Array.isArray(styleDetails) &&
							styleDetails.map((style, index) => (
								<Flex
									width="6.75rem"
									padding="1rem 0"
									alignItems="center"
									justifyContent="center"
									sx={{
										border: curStyleIndex === index ? "1px solid #000" : "none"
									}}
									onClick={() => {
										setCurStyleIndex(index)
									}}
								>
									<Image
										src={
											baseUrl +
											(curStyleImgs[index] ? curStyleImgs[index] : style.imgUrl)
										}
										sx={{
											width: "3.79rem",
											minWidth: "14px",
											minHeight: "14px"
										}}
									/>
								</Flex>
							))}
						{/* 
						<Flex
							width="6.75rem"
							height="4.88rem"
							alignItems="center"
							justifyContent="center"
						>
							<Image
								src="./4/style1.png"
								sx={{
									width: "3.89rem",
									minWidth: "14px",
									minHeight: "14px"
								}}
							/>
						</Flex> */}
					</Flex>

					<Flex flexDirection="column" flexGrow={1} pl="0.6rem">
						<Box width={[1]}>
							<Text fontSize="0.18rem" fontWeight="bolder">
								SECTION NUMBER
							</Text>
							<Text fontSize="0.1rem" m="0.16rem 0">
								{props.styleNo}
							</Text>
							<AisleButton text="A" {...props} />
						</Box>
						<ColorList
							colorList={styleDetails[curStyleIndex].plainColors}
							handleSelect={handleSelect}
						/>
						<PaintList
							paintList={styleDetails[curStyleIndex].flowerColors}
							handleSelect={handleSelect}
						/>
					</Flex>
				</Flex>
			</Box>
		</Modal>
	)
}
