import React, { useState, useEffect } from "react"
import { Flex, Text, Box, Image, Button } from "rebass"
import Head from "../components/nav"
import EditBox from "../components/made-edit-box"
import BigBox from "../components/made-big-box"
import StyleItem from "../components/made-style-item"
import { getPageQuery } from "../utils/helper"
import Router from "next/router"
import request from "../utils/request.js"
// import SelectAssignTool from "../components/select-assign-tool"
export default () => {
	const [styleDetails, setStyleDetails] = useState([])
	const [showBigBox, setShowBigBox] = useState(false)
	const [showEditBox, setShowEditBox] = useState(false)
	const boxNos = [0, 1, 2, 3, 4, 5]
	useEffect(() => {
		const getStyleDetails = async () => {
			let query = getPageQuery()
			console.log(query)
			let reqList = []
			const req = await request("style/detail", { _id: query.id }, "get")
			reqList.push(req)
			setStyleDetails(req.category)
			if (query.id1) {
				const req1 = await request("style/detail", { _id: query.id1 }, "get")
				reqList.push(req1)
			}
			setStyleDetails(reqList)
		}
		getStyleDetails()
	}, [])

	return (
		<>
			<Flex flexDirection="column">
				<Head></Head>
				<Flex flexDirection="column">
					<Flex
						justifyContent="space-around"
						alignItems="center"
						fontSize="0.23px"
						bg="#000"
						color="#fff"
						width="19.2rem"
						height="1.12rem"
					>
						<Text>TOPS</Text>
						<Text>BOTTOMS</Text>
					</Flex>
					<Flex flexWrap="wrap">
						{boxNos.map(boxNo => (
							<StyleItem
								styleList={styleDetails}
								openBigBox={() => {
									setShowBigBox(true)
								}}
								openEditBox={() => setShowEditBox(true)}
							/>
						))}
					</Flex>
					<Flex fontSize="0.27rem" color="#fff" justifyContent="">
						<Button
							height="1.13rem"
							width="9.6rem"
							bg="#000"
							variant="primary"
							padding="0"
							height="1.13rem"
							width="9.6rem"
							sx={{
								borderRadius: 0,
								cursor: "pointer"
							}}
						>
							{"< RETURN"}
						</Button>
						<Button
							height="1.13rem"
							width="9.6rem"
							bg="#FF8E6C"
							variant="primary"
							padding="0"
							height="1.13rem"
							width="9.6rem"
							sx={{
								borderRadius: 0,
								cursor: "pointer"
							}}
						>
							COMPLETE
						</Button>
					</Flex>
				</Flex>
				{showBigBox ? (
					<BigBox
						onClose={() => {
							setShowBigBox(false)
						}}
					/>
				) : null}
				{showEditBox ? (
					<EditBox
						styleDetails={styleDetails}
						onClose={() => {
							setShowEditBox(false)
						}}
					/>
				) : null}
			</Flex>
		</>
	)
}
