import React, { useEffect, useMemo } from "react"
import { ReactSVG } from "react-svg"
import { Image } from "rebass"
import { baseUrl } from "../../utils/helper"

export default props => {
	const { width, svgUrl, shadowUrl, key, colors, svgId } = props
	console.log(colors)
	let svgElement = false
	useEffect(() => {
		// if()
		if (!svgElement || !colors) return
		for (let i = 0; i < svgElement.children.length; i++) {
			if (
				svgElement.children[i].tagName === "g" ||
				svgElement.children[i].tagName === "path"
			) {
				svgElement.children[i].style.fill = colors[0].type
					? `url("#${colors[0]._id}")`
					: colors[0].value
			}
		}
	}, [colors])

	const Memo = useMemo(() => {
		return (
			<div
				style={{
					position: "relative",
					width: width
				}}
			>
				<Image
					src={baseUrl + shadowUrl}
					sx={{
						width: width,
						position: "absolute"
					}}
				/>
				<ReactSVG
					style={{
						width: width,
						minWidth: "14px"
					}}
					afterInjection={(error, svg) => {
						if (error) {
							console.error(error)
							return
						}
						if (colors && colors.length > 0) {
							for (let i = 0; i < svg.children.length; i++) {
								if (
									svg.children[i].tagName === "g" ||
									svg.children[i].tagName === "path"
								) {
									svg.children[i].style.fill = colors[0].type
										? `url("#${colors[0]._id}")`
										: colors[0].value
								}
							}
						}
					}}
					renumerateIRIElements={false}
					loading={() => {
						console.log("loading")
						return "loading"
					}}
					beforeInjection={svg => {
						svg.setAttribute("id", svgId || key)
						svg.setAttribute("style", `width: ${width}; height: 100%`)
					}}
					evalScripts="always"
					src={baseUrl + svgUrl}
				/>
			</div>
		)
	}, [svgUrl])
	return Memo
}
