import React, { useEffect, useState } from "react"
import { Flex, Text, Box, Image } from "rebass"

import Modal from "./modal"
import { ColorList, PaintList } from "./color-and-paint"

import request from "../utils/request.js"
import { baseUrl } from "../utils/helper"
export default props => {
	const { styleDetails } = props
	console.log("made-edit-box")
	console.log(styleDetails[0].flowerColors)
	const [curStyleIndex, setCurStyleIndex] = useState(0)
	const [curStyleImgs, setCurStyleImgs] = useState([false, false])
	// const [colorList, setColorList] = useState(styleDetails[0].plainColors)
	const handleSelect = (item, type) => {
		switch (type) {
			case "color":
				const cIndex = colorList.findIndex(x => x._id === item._id)

				curStyleImgs.splice(
					curStyleIndex,
					1,
					styleDetails[curStyleIndex].plainColors[cIndex].front
				)
				setColorList([].concat(colorList))
				break
			case "paint":
				const pIndex = paintList.findIndex(x => x._id === item._id)
				paintList[pIndex].selected = !paintList[pIndex].selected
				// colorList.splice(index, 1, item.selected=true)
				setPaintList([].concat(paintList))
				break
			default:
				break
		}
	}

	return (
		<Modal onClose={props.onClose}>
			<Box width="14rem" fontSize="0.18rem" color="#000">
				<Flex justifyContent="space-between">
					<Flex
						flexDirection="column"
						alignItems="center"
						justifyContent="space-evenly"
						width="6.75rem"
						sx={{
							cursor: "pointer"
						}}
					>
						{Array.isArray(styleDetails) &&
							styleDetails.map((style, index) => (
								<Flex
									width="6.75rem"
									padding="1rem 0"
									alignItems="center"
									justifyContent="center"
								>
									<Image
										src={
											baseUrl +
											(curStyleImgs[curStyleIndex] ? "" : style.imgUrl)
										}
										sx={{
											width: "3.79rem",
											minWidth: "14px",
											minHeight: "14px"
										}}
									/>
								</Flex>
							))}
						{/* 
						<Flex
							width="6.75rem"
							height="4.88rem"
							alignItems="center"
							justifyContent="center"
						>
							<Image
								src="./4/style1.png"
								sx={{
									width: "3.89rem",
									minWidth: "14px",
									minHeight: "14px"
								}}
							/>
						</Flex> */}
					</Flex>

					<Flex flexDirection="column" flexGrow={1} pl="0.6rem">
						<Box>
							<Text></Text>
							<Text></Text>
						</Box>
						<ColorList
							colorList={styleDetails[curStyleIndex].plainColors}
							handleSelect={() => {}}
						/>
						<PaintList
							paintList={styleDetails[curStyleIndex].flowerColors}
							handleSelect={() => {}}
						/>
					</Flex>
				</Flex>
			</Box>
		</Modal>
	)
}
